contracts/SampleERC20.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SBC Token
 * @dev Simple ERC20 Token for testing the SignatureVerifyingPaymaster
 */
contract SBC is ERC20, Ownable {
    // Decimals for token (standard 18)
    uint8 private constant DECIMALS = 18;
    // Initial supply of 1 million tokens
    uint256 private constant INITIAL_SUPPLY = 1_000_000;

    /**
     * @dev Constructor mints initial supply to deployer
     */
    constructor() ERC20("Stable Coin Test Token", "SBC") Ownable(msg.sender) {
        _mint(msg.sender, INITIAL_SUPPLY * 10**DECIMALS);
    }

    /**
     * @dev Mint new tokens (only owner)
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Public function to mint small amount of tokens for testing
     * @notice Limited to 100 tokens per mint to prevent abuse
     */
    function mintTestTokens() external {
        _mint(msg.sender, 100 * 10**DECIMALS);  // Mint 100 tokens
    }

    /**
     * @dev Override decimals to match constant
     */
    function decimals() public pure override returns (uint8) {
        return DECIMALS;
    }
}

contracts/SampleNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SampleNFT is ERC721, Ownable {
    uint256 private _tokenIds;

    // Base URI for metadata
    string private _baseTokenURI;

    constructor(
        string memory name,
        string memory symbol
    ) ERC721(name, symbol) Ownable(msg.sender) {}

    // Set base URI for metadata
    function setBaseURI(string memory baseURI) public onlyOwner {
        _baseTokenURI = baseURI;
    }

    // Override _baseURI internal function
    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    // Mint new NFT (anyone can call this function)
    function mintNFT(address recipient) public returns (uint256) {
        uint256 newItemId = _tokenIds + 1;
        _safeMint(recipient, newItemId);
        return newItemId;
    }
}

contracts/SignatureVerifyingPaymaster.so.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

// Import core ERC4337 paymaster interface
import "@account-abstraction/contracts/interfaces/IPaymaster.sol";
// Import OpenZeppelin's access control for owner-only functions
import "@openzeppelin/contracts/access/Ownable.sol";
// Import ERC20 interface for token balance checks
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// Import OpenZeppelin's ECDSA for signature verification
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
// Import OpenZeppelin's Pausable for emergency stops
import "@openzeppelin/contracts/utils/Pausable.sol";
// Import ERC4337 EntryPoint interface
import "@account-abstraction/contracts/interfaces/IEntryPoint.sol";

// Contract inherits from IPaymaster (ERC4337 interface), Ownable (for admin functions), and Pausable (for emergency stops)
contract SignatureVerifyingPaymaster is IPaymaster, Ownable, Pausable {
    // Enable ECDSA functions to be called directly on bytes32 variables
    using ECDSA for bytes32;

    // Address of the token that users must hold (set once during deployment)
    address public immutable SBC_TOKEN;
    // Required token balance users must hold (set once during deployment)
    uint256 public immutable REQUIRED_SBC_BALANCE;
    // Maximum time a signature can be valid for (7 days)
    uint256 public constant MAX_EXPIRY_DURATION = 7 days;

    // Address authorized to sign paymaster approvals
    address public authorizedSigner;
    // Mapping to track used nonces (prevents replay attacks)
    mapping(bytes32 => bool) public usedNonces;
    // Reference to the ERC4337 EntryPoint contract (set once during deployment)
    IEntryPoint public immutable entryPoint;

    // Event emitted when the authorized signer is changed
    event SignerUpdated(address indexed newSigner);
    // Event emitted when a nonce is marked as used
    event NonceUsed(bytes32 indexed nonceHash);
    // Event emitted when ETH is deposited to the paymaster
    event PaymasterDeposit(address indexed from, uint256 amount);
    // Event emitted when ETH is withdrawn from the paymaster
    event PaymasterWithdrawal(address indexed to, uint256 amount);
    // Event emitted when gas is paid for the operation
    event PaymasterPayment(uint256 actualGasCost, uint256 actualUserOpFeePerGas);

    // Constructor sets initial values that can't be changed later
    constructor(
        address _authorizedSigner,  // Address that will sign paymaster approvals
        address _entryPoint,        // ERC4337 EntryPoint contract address
        address _sbcToken,          // Token address that users must hold
        uint256 _requiredBalance    // Amount of tokens users must hold
    ) Ownable(msg.sender) {         // Set the deployer as the owner
        // Prevent zero address inputs
        require(_authorizedSigner != address(0), "Zero address signer");
        require(_entryPoint != address(0), "Zero address entryPoint");
        require(_sbcToken != address(0), "Zero address token");
        require(_requiredBalance > 0, "Zero balance requirement");

        // Initialize immutable state variables
        authorizedSigner = _authorizedSigner;
        entryPoint = IEntryPoint(_entryPoint);
        SBC_TOKEN = _sbcToken;
        REQUIRED_SBC_BALANCE = _requiredBalance;
    }

    /**
     * Allow owner to update the authorized signer address
     * @param _newSigner - New authorized signer address
     */
    function setSigner(address _newSigner) external onlyOwner {
        require(_newSigner != address(0), "Zero address signer");
        authorizedSigner = _newSigner;
        emit SignerUpdated(_newSigner);
    }

    /**
     * Main validation function called by the EntryPoint contract
     * @param userOp - The user operation to validate
     * @param userOpHash - Hash of the user operation (excluding signatures)
     * @param maxCost - Maximum cost of this operation (unused in this implementation)
     * @return context - Data to be passed to postOp
     * @return validationData - Packed validation data (0 means success)
     */
    function validatePaymasterUserOp(
        PackedUserOperation calldata userOp,  // The user operation to validate
        bytes32 userOpHash,                   // Hash of the user operation
        uint256 maxCost                       // Maximum possible gas cost for this operation
    ) external view whenNotPaused returns (bytes memory context, uint256 validationData) {
        // Ensure only the EntryPoint can call this function
        require(msg.sender == address(entryPoint), "Caller not EntryPoint");

        // Verify paymaster has enough deposit to cover max possible gas cost
        require(entryPoint.balanceOf(address(this)) >= maxCost, "Insufficient paymaster deposit");

        // Extract and validate the paymaster data format
        // Format: paymasterAddress(20) | nonce(32) | expiry(32) | signature(65) = 149 bytes
        require(userOp.paymasterAndData.length == 149, "Invalid paymasterAndData length");
        bytes memory paymasterAndData = userOp.paymasterAndData;

        // Extract nonce and expiry from the packed data
        uint256 nonce = uint256(bytes32(slice(paymasterAndData, 20, 32)));    // Skip first 20 bytes (paymaster address)
        uint256 expiry = uint256(bytes32(slice(paymasterAndData, 52, 32)));   // Skip 20 + 32 bytes

        // Verify nonce hasn't been used (prevents replay attacks)
        bytes32 nonceHash = keccak256(abi.encode(nonce));
        require(!usedNonces[nonceHash], "Nonce already used");

        // Verify timestamp-based validity
        require(expiry > block.timestamp, "Signature expired");
        require(expiry <= block.timestamp + MAX_EXPIRY_DURATION, "Expiry too far in future");

        // Check if user has required token balance
        require(
            IERC20(SBC_TOKEN).balanceOf(userOp.sender) >= REQUIRED_SBC_BALANCE,
            "Insufficient SBC balance"
        );

        // Extract signature from packed data
        bytes memory signature = slice(paymasterAndData, 84, 65);  // Skip 20 + 32 + 32 bytes

        // Create message hash for signature verification
        // Include chainId to prevent cross-chain replay attacks
        bytes32 messageHash = keccak256(abi.encode(
            block.chainid,
            userOp.sender,
            nonce,
            expiry,
            userOpHash
        ));

        // Verify signature was created by authorized signer
        address recovered = ECDSA.recover(messageHash, signature);
        require(recovered == authorizedSigner, "Invalid signature");

        // Return context for postOp and validation success
        context = abi.encode(nonceHash);
        validationData = 0;  // 0 indicates successful validation

        return (context, validationData);
    }

    /**
     * Post-operation handler called by EntryPoint after operation execution
     * @param mode - Success/failure status of the operation
     * @param context - Context data passed from validatePaymasterUserOp
     * @param actualGasCost - Actual gas cost of the operation (unused)
     * @param actualUserOpFeePerGas - Actual gas price used (unused)
     */
    function postOp(
        PostOpMode mode,                          // Operation execution status
        bytes calldata context,                   // Context from validatePaymasterUserOp
        uint256 actualGasCost,               // Actual gas cost (unused)
        uint256 actualUserOpFeePerGas        // Actual gas price (unused)
    ) external {
        // Ensure only the EntryPoint can call this function
        require(msg.sender == address(entryPoint), "Caller not EntryPoint");

        // Only mark nonce as used if operation succeeded
        if (mode == PostOpMode.opSucceeded) {
            bytes32 nonceHash = abi.decode(context, (bytes32));
            usedNonces[nonceHash] = true;
            emit NonceUsed(nonceHash);
            emit PaymasterPayment(actualGasCost, actualUserOpFeePerGas);
        }
    }

    /**
     * Allow anyone to deposit ETH to fund paymaster operations
     */
    function deposit() external payable {
        entryPoint.depositTo{value: msg.value}(address(this));
        emit PaymasterDeposit(msg.sender, msg.value);
    }

    /**
     * Allow owner to withdraw ETH from the paymaster's deposit
     * @param withdrawAddress - Address to send the withdrawn ETH
     * @param amount - Amount of ETH to withdraw
     */
    function withdrawTo(address payable withdrawAddress, uint256 amount) external onlyOwner {
        require(withdrawAddress != address(0), "Zero address withdraw");
        require(amount > 0, "Zero amount withdraw");
        entryPoint.withdrawTo(withdrawAddress, amount);
        emit PaymasterWithdrawal(withdrawAddress, amount);
    }

    /**
     * View function to check current deposit balance in EntryPoint
     * @return Current deposit balance
     */
    function getDeposit() public view returns (uint256) {
        return entryPoint.balanceOf(address(this));
    }

    /**
     * Allow owner to pause the paymaster in emergency
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * Allow owner to unpause the paymaster
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * Internal helper function to extract a slice from a bytes array
     * @param data - Source bytes array
     * @param start - Starting position
     * @param length - Length of slice to extract
     * @return Extracted bytes slice
     */
    function slice(bytes memory data, uint256 start, uint256 length) internal pure returns (bytes memory) {
        require(start + length <= data.length, "Slice out of bounds");
        bytes memory result = new bytes(length);
        assembly {
            // Get pointers to memory locations
            let resultPtr := add(result, 32)         // Skip length prefix
            let dataPtr := add(add(data, 32), start) // Skip length prefix and offset
            // Copy length bytes from dataPtr to resultPtr
            mstore(resultPtr, mload(dataPtr))
        }
        return result;
    }
}

